import React from "react";
import "./LineGraph.css";
// import { Line as LineJS } from 'chart.js/auto'
// import { Line } from "react-chartjs-2";

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Line } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);



function LineGraph() {

  const dataVals = 
  [{
      x: 10, 
      y: 23
    }, {
      x:15, 
      y:10
    }, {
      x:20, 
      y:22
    }
  ]
  const labels = dataVals.map(dataVal => dataVal.x);

  const data = {
    labels: labels,
    datasets: [
      {
        label: "test", 
        type: "line",
        data: dataVals,
        fill: true,
        backgroundColor: "#071106",
        borderColor: "#49B540",
        borderWidth:2,
        pointBorderColor: 'rgba(0,0,0,0)',
        pointBackgroundColor: 'rgba(0,0,0,0)',
        pointHoverBackgroundColor: "#49B540",
        pointHoverBorderColor: '#000000',
        pointHoverBorderWidth: 4,
        pointHoverRadius: 6,
      }
    ]
  }
  const options = {
    plugins:{
      legend: {
      display: false
      }
    }
    ,
    scales: { 
      xAxes: {
        display: false,
        ticks: {
          display: false
        }
      },
      yAxes: {
        // display: false,
        // ticks: {
        //   display: false
        // }
      }
    }
  }

  return (
    <div className="linegraph">
      <Line data={data} options={options}/>
    </div>
  );
}

export default LineGraph;
